        $page = isset($_POST['page']) ? intval($_POST['page']) : 1;
        $rows = isset($_POST['rows']) ? intval($_POST['rows']) : 10;
	    $offset = ($page-1)*$rows;

		$kriteria=$this->input->post('cari');
		$where='';
		if($kriteria <> ''){
			$where="where upper(nm_skpd) like upper('%$kriteria%')";
		}


	    $result = array();	
	    $rs = mysql_query("select count(*) from ms_skpd $where");
	    $row = mysql_fetch_row($rs);
	    $result["total"] = $row[0];
	
	    $rs = mysql_query("select * from ms_skpd $where order by kd_skpd limit $offset,$rows ");
 
	    $rows = array();
	    while($row = mysql_fetch_object($rs)){
		  array_push($rows, $row);
	    }
	    $result["rows"] = $rows;
	
	    echo json_encode($result);
      
	}
    
