   function select_real($kegiatan='',$skpd='') {

		$sql = "select a.kd_rek5,b.nm_rek5,a.nilai,a.nilai_ubah,a.sumber_ubah as sumber from trdrka a inner join ms_rek5 b on a.kd_rek5=b.kd_rek5 where kd_kegiatan='$kegiatan' order by a.kd_rek5";                   


        $query1 = $this->db->query($sql);  
        $result = array();
        $ii = 0;
        foreach($query1->result_array() as $resulte)
        { 
				$rk=$resulte['kd_rek5'];

				$query = $this->db->query(" SELECT SUM(a.nilai) AS nilai FROM trdspp a 
											LEFT JOIN trhspp b ON a.no_spp=b.no_spp
											LEFT JOIN trhspm c ON c.no_spp=b.no_spp
											LEFT JOIN trhsp2d d ON d.no_spm=c.no_spm
											WHERE a.kd_skpd='$skpd' AND a.kd_kegiatan='$kegiatan' AND a.kd_rek5='$rk' ");  
				foreach($query->result_array() as $res){
					$sp2d=$res['nilai'];
				}

				$query = $this->db->query("SELECT SUM(a.rupiah) AS nilai FROM trdkasin_pkd a LEFT JOIN trhkasin_pkd b ON a.no_sts=b.no_sts and a.kd_skpd=b.kd_skpd WHERE a.kd_skpd='$skpd' AND b.kd_kegiatan='$kegiatan' AND a.kd_rek5='$rk' ");  
				foreach($query->result_array() as $res){
					$cp=$res['nilai'];
				}
				

				$result[] = array(
							'id' => $ii,        
							'kd_rek5' => $resulte['kd_rek5'],  
