                    (SELECT IFNULL(SUM(b.nilai),0) AS nilai FROM trhsp2d a INNER JOIN trdspp b ON a.no_spp=b.no_spp 
                        WHERE a.jns_spp IN('1','2','3','7') AND a.kd_skpd='$kode' AND a.no_kas<>'')-
