function cek_s() {
        $result = array();
        $row = array();
        $page = isset($_POST['page']) ? intval($_POST['page']) : 1;
        $rows = isset($_POST['rows']) ? intval($_POST['rows']) : 10;
        $offset = ($page-1)*$rows;
        
        $kriteria = '';
        $kriteria = $this->input->post('cari');
        $where ='';
        if ($kriteria <> ''){                               
            $where="where  (upper(a.kd_skpd) like upper('%$kriteria%') or upper(a.nm_skpd like upper('%$kriteria%'))
                    ) ";            
        }
        
        $sql = "SELECT count(*) as tot from ms_skpd a $where" ;
        $query1 = $this->db->query($sql);
        $total = $query1->row();
        
        $sql = "SELECT kd_skpd,nm_skpd, (SELECT MONTH(MAX(tgl_bukti)) FROM trhtransout z INNER JOIN trdtransout zz ON zz.no_bukti=z.no_bukti WHERE z.kd_skpd=a.kd_skpd)AS transterakhir
FROM ms_skpd a $where order by a.kd_skpd limit $offset,$rows";
    
        $query1 = $this->db->query($sql);  
        $result = array();
        $ii = 0;
        foreach($query1->result_array() as $resulte)
        { 
        

            $row[] = array(
                        'id' => $ii,
                        'kd' => $resulte['kd_skpd'],
                        'nm' =>$resulte['nm_skpd'],
                        'c' => $resulte['transterakhir']
                                                );
                        $ii++;
        }
        
        $result["total"] = $total->tot;
        $result["rows"] = $row; 
        echo json_encode($result);
        $query1->free_result();
           
    }
    
