	function skpd_l4(){
		//$sql="select * from ms_skpd order by kd_skpd";
		$lccr = $this->input->post('q');
        $sql = "SELECT kd_skpd,nm_skpd FROM ms_skpd where upper(kd_skpd) like upper('%$lccr%') or upper(nm_skpd) like upper('%$lccr%') order by kd_skpd";
		$mysql=$this->db->query($sql);
		$result=array();
		foreach($mysql->result_array() as $resulte){
			$result[]=array(
					'kd_skpd' => $resulte['kd_skpd'],
					'nm_skpd' => $resulte['nm_skpd']
			);
		}
		echo json_encode($result);
	}

