		function angka($nilai){

//$this->angka($surplus89lalu);
	if($nilai<0){
		$lc = '('.number_format(abs($nilai),2,',','.').')';
	}else{
		if($nilai==0){
			$lc ='0,00';
		}else{
			$lc = number_format($nilai,2,',','.');
		}
	}

	return $lc;
}




