    function _mpdf($judul='',$isi='',$lMargin='',$rMargin='',$font=10,$orientasi='',$skpd='',$hal='') {
        
        ini_set("memory_limit","-1");
        $this->load->library('mpdf');

        $this->mpdf->defaultheaderfontsize = 6;	/* in pts */
        $this->mpdf->defaultheaderfontstyle = BI;	/* blank, B, I, or BI */
        $this->mpdf->defaultheaderline = 1; 	/* 1 to include line below header/above footer */
		
        $this->mpdf->defaultfooterfontsize = 7;	/* in pts */
        $this->mpdf->defaultfooterfontstyle = B;	/* blank, B, I, or BI */
        $this->mpdf->defaultfooterline = 1; 
		$bMargin=12;
		$tMargin=10;
        //$this->mpdf->SetFooter('Printed on @ {DATE j-m-Y H:i:s} |Simakda| Page {PAGENO} of {nb}');
		$initial='';
        $this->mpdf->SetFooter(''.$initial.' ||'.$skpd.' Halaman  : {PAGENO}| ');

        $this->mpdf->AddPage($orientasi,'',$hal,'1','off',$lMargin,$rMargin,$tMargin,$bMargin);

        if (!empty($judul)) $this->mpdf->writeHTML($judul);
        $this->mpdf->writeHTML($isi);         
        $this->mpdf->Output();
               
    }

