		if ($stsubah==0){
			$field="nilai";		
			$sts="susun";		
		}else{
			$field="nilai";				
			$sts="ubah";		
		}
         $sql = "SELECT SUM($field) as nilai FROM trdskpd  WHERE kd_skpd='$skpd' and bulan>=$awal AND bulan<=$ahir and status='$sts' ";
				
        $query1 = $this->db->query($sql);  
		$test   = $query1->num_rows();
        $ii     = 0;
        
        foreach($query1->result_array() as $resulte)
        { 
            $result = array(
                        'id' => $ii,        
                        'total' => $resulte['nilai']
                        );
                        $ii++;
