	function titik($str){
		$panjang=105;
		if($panjang>=strlen($str)){
			$selisih=$panjang-strlen($str);
			for($i=1;$i<=$selisih;$i++){
				$str .='.';
			}
			$hasil=$str;
		}else{
			$hasil=substr($str,0,105);
		} 
		return $hasil;
	}

